// Load all data
const supabaseCredentials = $('Supabase credentials').first();
const extractRows = $items('Extract from File');
const modelOutputs = $items('APT Model2');

// Sanity check
if (!supabaseCredentials || extractRows.length === 0 || modelOutputs.length === 0) {
  throw new Error('Missing input data. Ensure Supabase credentials, Extract from File, and APT Model2 all produced output.');
}

const supabaseUrl = supabaseCredentials.json.supabase_url;
const supabaseKey = supabaseCredentials.json.supabase_key;

// Field mapping
const keyMap = {
  src_ip: 'src_ip',
  dst_ip: 'dst_ip',
