  totlen_fwd_pkts: 'total_length_of_fwd_packet',
  totlen_bwd_pkts: 'total_length_of_bwd_packet',
  fwd_pkt_len_max: 'fwd_packet_length_max',
  fwd_pkt_len_min: 'fwd_packet_length_min',
  fwd_pkt_len_mean: 'fwd_packet_length_mean',
  fwd_pkt_len_std: 'fwd_packet_length_std',
  bwd_pkt_len_max: 'bwd_packet_length_max',
  bwd_pkt_len_min: 'bwd_packet_length_min',
  bwd_pkt_len_mean: 'bwd_packet_length_mean',
  bwd_pkt_len_std: 'bwd_packet_length_std',
  flow_iat_mean: 'flow_iat_mean',
  flow_iat_std: 'flow_iat_std',
  flow_iat_max: 'flow_iat_max',
  flow_iat_min: 'flow_iat_min',
  fwd_iat_tot: 'fwd_iat_total',
  fwd_iat_mean: 'fwd_iat_mean',
  fwd_iat_std: 'fwd_iat_std',
  fwd_iat_max: 'fwd_iat_max',
  fwd_iat_min: 'fwd_iat_min',
  bwd_iat_tot: 'bwd_iat_total',
  bwd_iat_mean: 'bwd_iat_mean',
  bwd_iat_std: 'bwd_iat_std',
  bwd_iat_max: 'bwd_iat_max',
  bwd_iat_min: 'bwd_iat_min'
};

const results = [];

for (let i = 0; i < extractRows.length; i++) {
  const source = extractRows[i].json;
  const modelOutput = modelOutputs[i]?.json;

  if (!modelOutput) continue;

  const prediction = modelOutput.prediction;
  const probabilities = modelOutput.probabilities || [];
  const prob = typeof prediction === 'number' ? probabilities[prediction] ?? null : null;
  const isNormal = prediction === 3;
  const tableName = isNormal ? "normal_traffic_apt" : "attack_traffic_apt";

  const row = {};
  for (const [inputKey, dbKey] of Object.entries(keyMap)) {
    let val = source[inputKey];
    if (val === undefined || val === null) {
      row[dbKey] = null;
    } else if (!isNaN(val)) {
      row[dbKey] = Number(val);
    } else {
      row[dbKey] = val;
    }
  }

  // Add label/prob only for attack table
  if (!isNormal) {
    row.Label = prediction;
    row.Prob = prob;
  }

  results.push({
    json: {
      supabase_url: supabaseUrl,
      supabase_key: supabaseKey,
      table: tableName,
      payload: row,
      pred: prediction,
      prob: prob
    }
  });
}

return results;

